
public class Boolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          boolean same ;
          
          String s1="Acad";
          String s2="Acad";
          if(s1==s2)   //IF both the strings are equal then print true
          {  
        	  
        	  same=true;
        	  System.out.println(same);
        	  
          }else           //If not same then print false 
          {
        	  same=false;
        	  System.out.println(same);
          }
          
	}

}
